mapper.select:
SELECT hn.id, hn.work_id AS workId, hn.name, hnpt.nurse_profashion_title AS profashionTitle, hdpt.name AS departmentName, hn.major_direct AS majorDirect, hn.phone, hn.enter_date AS enterDate, hn.status FROM hospital.h_nurse_tb hn LEFT JOIN hospital.h_clinic_room_nurse_tb hcn ON hn.id = hcn.nurse_id LEFT JOIN hospital.h_clinic_room_tb hc ON hcn.clinic_room_id = hc.id LEFT JOIN hospital.h_department_tb hdpt ON hdpt.id = hc.department_id LEFT JOIN hospital.h_nurse_profashion_title_tb hnpt ON hnpt.id = hn.npt_id
AND hn.work_id LIKE CONCAT('%', #{workId}, '%')
AND hn.name LIKE CONCAT('%', #{name}, '%')
AND hdpt.name LIKE CONCAT('%', #{departmentName}, '%')
AND hn.status = #{status}
AND hn.npt_id = #{nptId}
ORDER BY hn.id DESC LIMIT #{start}, #{size}

SELECT COUNT(1) FROM hospital.h_nurse_tb hn LEFT JOIN hospital.h_clinic_room_nurse_tb hcn ON hn.id = hcn.nurse_id LEFT JOIN hospital.h_clinic_room_tb hc ON hcn.clinic_room_id = hc.id LEFT JOIN hospital.h_department_tb hdpt ON hdpt.id = hc.department_id LEFT JOIN hospital.h_nurse_profashion_title_tb hnpt ON hnpt.id = hn.npt_id
AND hn.work_id LIKE CONCAT('%', #{workId}, '%')
AND hn.name LIKE CONCAT('%', #{name}, '%')
AND hdpt.name LIKE CONCAT('%', #{departmentName}, '%')
AND hn.status = #{status}
AND hn.npt_id = #{nptId}
